export default class GingerTransports {

  /**
   * Constructor
   * @param {Array} transports 
   */
  constructor(transports) {
    this.list = transports || {};
  }

  /**
   * Return the http utility
   * @return {Axios}
   */
  get http() {
    return this.list.http || null;
  }

  /**
   * Return a socket utility
   * 
   * @return {Object | null}
   */
  get socket() {
    return this.list.socket || null;
  }

  /**
   * Return the connection matching the given name
   * @param {String} name 
   * @return {*}
   */
  getByName(name) {
    let ret = this.http;

    if (name) {
      ret = this.list.hasOwnProperty(name) ? this.list[name] : null;
    }

    return ret;
  }
}

// initial state
const state = {
  routes: [],
  modules: [],
  error: []
}

// getters
const getters = {
  getRoutes: state => state.routes
}

// actions
const actions = {
  register({commit}, module){
    commit('addModule', module);
  },

  error({commit}, error){
    commit('addError', error);
  },

  setRoutes({commit}, routes){
    commit('addRoutes', routes);
  },

  getRoutes({ state }){
    return Promise.resolve(state.routes);
  }
}

// mutations
const mutations = {
  addRoutes( state, routes) {
    state.routes = state.routes.concat(routes);
  },

  addModule( state, module ){
    state.modules.push(module);
  },

  addError( state, error ){
    state.error.push(error);
  }
}

export default {
  namespaced: true,
  state,
  getters,
  actions,
  mutations
}

class Level {
  static get OFF(){ return {name: 'OFF', value: 0} };
  static get TRACE(){ return {name: 'TRACE', value: 1} };
  static get DEBUG(){ return {name: 'DEBUG', value: 2} };
  static get INFO(){ return {name: 'INFO', value: 4} };
  static get TIME(){ return {name: 'TIME', value: 8} };
  static get WARN(){ return {name: 'WARN', value: 16} };
  static get ERROR(){ return {name: 'ERROR', value: 32} };
  static get ALL(){ return {name: 'ALL', value: 63} };

  /**
   * Get the level from its value
   * @param  {Int} value The level value
   * @return {Level|null}     The level object or null
   */
  static getFromValue(value){
    let ret = null;
    const list = [Level.OFF, Level.TRACE, Level.DEBUG, Level.INFO, Level.TIME, Level.WARN, Level.ERROR, Level.ALL];
    list.forEach((e, i) => {
      if (e.value === value){
        ret = e;
      }
    })

    return ret;
  }
}

export default Level

